import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Navbar.css';
import './Profile.css';
import uk from '../../assets/uk.png';
import HomeIcon from '@mui/icons-material/Home';
import { useParams } from "react-router-dom";
import EditIcon from '@mui/icons-material/Edit';

const translationMap = {
  username: 'Kullanıcı Adı',
  firstName: 'İsim',
  lastName: 'Soyisim',
  dateOfBirth: 'Doğum Tarihi',
  gender: 'Cinsiyet',
  email: 'E-posta',
  phoneNumber: 'Telefon',
  interests: 'İlgi Alanları',
};

function Profile() {
  const { userId } = useParams(); // URL'deki userId'yi al
  const [isDropdownOpen, setDropdownOpen] = useState(false);
  const [user, setUser] = useState(null); // Kullanıcı verisi
  const [error, setError] = useState(null); // Hata durumu
  const [isLoading, setIsLoading] = useState(true); // Yüklenme durumu
  const [editingField, setEditingField] = useState(null); // Hangi alan düzenleniyor
  

  useEffect(() => {
    // Kullanıcı verisini al
    axios
      .get(`/users/${userId}`, {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((response) => {
        setUser(response.data); // Kullanıcı verisini kaydet
        setIsLoading(false);   // Yüklenme durumunu güncelle
      })
      .catch((err) => {
        console.error("Kullanıcı verisi alınırken hata:", err);
        setError("Kullanıcı verisi alınamadı.");
        setIsLoading(false);
      });
  }, [userId]);

  const [editData, setEditData] = useState({
    username: user?.username,
    firstName: user?.firstName,
    lastName: user?.lastName,
    dateOfBirth: user?.dateOfBirth,
    gender: user?.gender,
    email: user?.email,
    phoneNumber: user?.phoneNumber,
    interests: user?.interests,
  });

  if (isLoading) {
    return <div>Yükleniyor...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  const toggleDropdown = () => {
    setDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = () => {
    window.location.href = '/login';
  };

  const handleProfile = () => {
    window.location.href = `/profile/${userId}`;
  };

  

  const handleEditClick = (field) => {
    setEditingField(field); // Düzenlenecek alanı ayarla
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSave = () => {
    setEditingField(null); // Düzenleme bitti
    console.log('Save changes'); // Şimdilik sadece konsola yazıyor
  };
  
  return (
    <div className="">
      {/* Navbar Bileşeni */}
      <nav className="navbar">
        <div className="navbar-left">
          <img
            src={uk}
            alt="Profile"
            className="profile-photo"
          />
          <HomeIcon className="profile-photo" />
          <button className="home-button" onClick={() => window.location.href = `/home/${userId}`}>Home</button>
        </div>
        <div className="navbar-right">
          <button className="add-event-button">Etkinlik Ekle</button>
          <div className="profile-menu">
            <img
              src={`/${user.profilePicturePath}`}
              alt="Profile"
              className="profile-photo"
              onClick={toggleDropdown}
            />
            <div className={`dropdown-menu ${isDropdownOpen ? 'show' : ''}`}>
              <button onClick={handleProfile}>Profilim</button>
              <button onClick={handleLogout}>Çıkış</button>
            </div>
          </div>
        </div>
      </nav>    

      {/* Etkinlikler Listesi */}
      <div className="card-container">
        <div className="profile-gap">
          <img
            src={`/${user.profilePicturePath}`}
            alt="Profile"
            className="profile-photo-update"
          />
          <h3 className="profile-name">{user?.firstName} {user?.lastName}</h3>
        </div>

        <div className="profile-info">
        {Object.keys(translationMap).map((field) => (
            <p key={field}>
              <strong>{translationMap[field]}:</strong>
              {editingField === field ? (
                <>
                  <input
                    type="text"
                    name={field}
                    value={editData[field]} // Set the input value to the state
                    onChange={handleInputChange} // Update the state when the input changes
                  />
                  <button onClick={handleSave}>Kaydet</button>
                </>
              ) : (
                <>
                  <span>{editData[field]}</span> {/* Display the value from state */}
                  <EditIcon
                    className="edit-icon"
                    onClick={() => handleEditClick(field)}
                  />
                </>
              )}
            </p>
          ))}
        </div>
      </div>           
      
    </div>
  );
}

export default Profile;
