import profilePic from './logo192.png'
import './Event.css'; // Add styles in a separate CSS file
import React from "react";

function Event() {
  
  return (
    <div className="card-container">
      <Card
          const imgSrc="https://picsum.photos/id/201/300/200"
          const imgAlt="Card Image 1"
          const title="Card Title"
          const description="This is the card description section. You can add more details about the product here"
        />
      {imgSrc && imgAlt && (
        <img src={imgSrc} alt={imgAlt} className="card-img" />
      )}
      {title && <h1 className="card-title">{title}</h1>}
      {description && <p className="card-description">{description}</p>}
      {buttonText && link && (
        <a href={link} className="card-btn">
          {buttonText}
        </a>
      )}
    </div>
  );
}

export default Event;
