import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import './Home.css';
import './Navbar.css';
import { useParams } from "react-router-dom";
import uk from '../../assets/uk.png'; 

const mapContainerStyle = {
  width: '100%',
  height: '200px',
};

function Home() {
  const { userId } = useParams(); // URL'deki userId'yi al
  const [events, setEvents] = useState([]);
  const [isDropdownOpen, setDropdownOpen] = useState(false);
  const [error, setError] = useState(null); // Hata durumu
  const [isLoading, setIsLoading] = useState(true); // Yüklenme durumu

  // Etkinlikleri almak için API çağrısı
  useEffect(() => {
    axios.get("/events")
      .then(response => {
        const eventsWithCoordinates = response.data.map(event => {
          // Adresi koordinatlara çevirmek için Google Geocoding API'sini kullanıyoruz
          return getCoordinates(event.location).then(coords => {
            return { ...event, latitude: coords.lat, longitude: coords.lng };
          });
        });

        // Promisleri bekle ve verileri güncelle
        Promise.all(eventsWithCoordinates).then(updatedEvents => {
          setEvents(updatedEvents);
        });
      })
      .catch(error => {
        console.error("Etkinlik bilgileri alınamadı", error);
      });
  }, []);

  // Adresi koordinatlara çevirme işlemi
  const getCoordinates = async (address) => {
    const googleMapsApiKey = "AIzaSyCMkmz6balxwr794m2BGcwQia1BsJ1AHZw";
    const geocodeUrl = `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(address)}&key=${googleMapsApiKey}`;

    try {
      const response = await axios.get(geocodeUrl);
      const location = response.data.results[0]?.geometry.location;
      if (location) {
        return { lat: location.lat, lng: location.lng };
      }
      return { lat: 0, lng: 0 }; // Eğer koordinatlar alınamazsa
    } catch (error) {
      console.error("Geocoding API hatası:", error);
      return { lat: 0, lng: 0 }; // Hata durumunda koordinatlar döndürülür
    }
  };

  if (isLoading) {
    return <div>Yükleniyor...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  const toggleDropdown = () => {
    setDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = () => {
    window.location.href = '/login';
  };

  if (events.length === 0) {
    return <div>Yükleniyor...</div>;
  }

  useEffect(() => {
    // Kullanıcı verisini al
    axios
      .get(`/users/${userId}`, {
        headers: {
          "Content-Type": "application/json",
        },
      })
      .then((response) => {
        setUser(response.data); // Kullanıcı verisini kaydet
        setIsLoading(false);   // Yüklenme durumunu güncelle
      })
      .catch((err) => {
        console.error("Kullanıcı verisi alınırken hata:", err);
        setError("Kullanıcı verisi alınamadı.");
        setIsLoading(false);
      });
  }, [userId]);

  return (
    <div className="page-container">
      {/* Navbar Bileşeni */}
      <nav className="navbar">
        <div className="navbar-left">
        <img
              src={uk} // Örnek profil fotoğrafı URL'si
              alt="Profile"
              className="profile-photo"
         />
          <button className="home-button" onClick={() => window.location.href = `/home/${userId}`}>Home</button>
        </div>
        <div className="navbar-right">
          <button className="add-event-button">Etkinlik Ekle</button>
          <div className="profile-menu">
            <img
              src="https://via.placeholder.com/40" // Örnek profil fotoğrafı URL'si
              alt="Profile"
              className="profile-photo"
              onClick={toggleDropdown}
            />
            <div className={`dropdown-menu ${isDropdownOpen ? 'show' : ''}`}>
              <p>Profilim</p>
              <button onClick={handleLogout}>Çıkış</button>
            </div>
          </div>
        </div>
      </nav>

      {/* Etkinlikler Listesi */}
      <div className="Home">
        <div className="event-list">
          {events.map((event) => (
            <div key={event.eventId} className="card-container">
              <h2>{event.eventName}</h2>
              <p>{event.description}</p>
              <p><strong>Tarih:</strong> {event.date}</p>
              <p><strong>Konum:</strong> {event.location}</p>
              
              <div className="map-container">
                <LoadScript googleMapsApiKey="AIzaSyCMkmz6balxwr794m2BGcwQia1BsJ1AHZw">
                  <GoogleMap
                    mapContainerStyle={mapContainerStyle}
                    center={{
                      lat: event.latitude,  // Etkinlik için enlem bilgisi
                      lng: event.longitude, // Etkinlik için boylam bilgisi
                    }}
                    zoom={14}
                  >
                    <Marker
                      position={{
                        lat: event.latitude,
                        lng: event.longitude,
                      }}
                    />
                  </GoogleMap>
                </LoadScript>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
