import React, { useState } from 'react';
import HomeIcon from '@mui/icons-material/Home'; // Kendi ikonunuza göre import edin
import './navbar.css';

  const Navbar = ({ userId, profilePicturePath }) => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const handleProfile = () => {
    window.location.href = `/profile/${userId}`;
  };

  const handleLogout = () => {
    window.location.href = '/login';
    console.log('Çıkış yapıldı');
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <img
          src="/path/to/default/profile/photo.jpg" // Varsayılan bir fotoğraf olabilir
          alt="Profile"
          className="profile-photo"
        />
        <HomeIcon className="profile-photo" />
        <button
          className="home-button"
          onClick={() => window.location.href = `/home/${userId}`}
        >
          Home
        </button>
      </div>
      <div className="navbar-right">
        <button className="add-event-button">Etkinlik Ekle</button>
        <div className="profile-menu">
          <img
            src={profilePicturePath || '/path/to/default/photo.jpg'} // Dinamik resim yolu
            alt="Profile"
            className="profile-photo"
            onClick={toggleDropdown}
          />
          <div className={`dropdown-menu ${isDropdownOpen ? 'show' : ''}`}>
            <button onClick={handleProfile}>Profilim</button>
            <button onClick={handleLogout}>Çıkış</button>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
