import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import Navbar from './Navbar'; // Navbar bileşeni burada dahil ediliyor
import './Home.css';
import { useParams } from 'react-router-dom';

const mapContainerStyle = {
  width: '100%',
  height: '250px',
};

function Home() {
  const { userId } = useParams(); // URL'deki userId'yi al
  const [events, setEvents] = useState([]);
  const [user, setUser] = useState(null); // Kullanıcı verisi
  const [error, setError] = useState(null); // Hata durumu
  const [isLoading, setIsLoading] = useState(true); // Yüklenme durumu
  const [expandedCard, setExpandedCard] = useState(null);

  // Etkinlikleri almak için API çağrısı
  useEffect(() => {
    axios
      .get('/events')
      .then((response) => {
        const eventsWithCoordinates = response.data.map((event) =>
          getCoordinates(event.location).then((coords) => ({
            ...event,
            latitude: coords.lat,
            longitude: coords.lng,
          }))
        );

        // Promisleri bekle ve verileri güncelle
        Promise.all(eventsWithCoordinates).then((updatedEvents) => {
          setEvents(updatedEvents);
        });
      })
      .catch((error) => {
        console.error('Etkinlik bilgileri alınamadı', error);
      });
  }, []);

  // Adresi koordinatlara çevirme işlemi
  const getCoordinates = async (address) => {
    const googleMapsApiKey = 'AIzaSyCMkmz6balxwr794m2BGcwQia1BsJ1AHZw';
    const geocodeUrl = `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(
      address
    )}&key=${googleMapsApiKey}`;

    try {
      const response = await axios.get(geocodeUrl);
      const location = response.data.results[0]?.geometry.location;
      if (location) {
        return { lat: location.lat, lng: location.lng };
      }
      return { lat: 0, lng: 0 }; // Eğer koordinatlar alınamazsa
    } catch (error) {
      console.error('Geocoding API hatası:', error);
      return { lat: 0, lng: 0 }; // Hata durumunda koordinatlar döndürülür
    }
  };

  useEffect(() => {
    // Kullanıcı verisini al
    axios
      .get(`/users/${userId}`, {
        headers: {
          'Content-Type': 'application/json',
        },
      })
      .then((response) => {
        setUser(response.data); // Kullanıcı verisini kaydet
        setIsLoading(false); // Yüklenme durumunu güncelle
      })
      .catch((err) => {
        console.error('Kullanıcı verisi alınırken hata:', err);
        setError('Kullanıcı verisi alınamadı.');
        setIsLoading(false);
      });
  }, [userId]);

  if (isLoading) {
    return <div>Yükleniyor...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  if (events.length === 0) {
    return <div>Yükleniyor...</div>;
  }

  const toggleExpand = (eventId) => {
    setExpandedCard(expandedCard === eventId ? null : eventId);
  };

  return (
    <div>
      {/* Navbar Bileşeni */}
      <Navbar
        userId={userId}
        profilePicturePath={`/${user.profilePicturePath}`}
      />

      {/* Etkinlikler Listesi */}
      <div className="">
        {events.map((event) => (
          <div key={event.eventId} className="card-container">
            <h2 className="event-info">{event.eventName}</h2>
            <p className="event-info">
              <strong>Tarih:</strong> {event.date}
            </p>
            <p className="event-info">
              <strong>Kategori:</strong> {event.category}
            </p>

            {/* Harita */}
            <div className="map-container">
              <LoadScript googleMapsApiKey="AIzaSyCMkmz6balxwr794m2BGcwQia1BsJ1AHZw">
                <GoogleMap
                  mapContainerStyle={mapContainerStyle}
                  center={{
                    lat: event.latitude,
                    lng: event.longitude,
                  }}
                  zoom={14}
                >
                  <Marker
                    position={{
                      lat: event.latitude,
                      lng: event.longitude,
                    }}
                  />
                </GoogleMap>
              </LoadScript>
            </div>

            {/* Genişletme İkonu */}
            <div
              className="expand-icon-container"
              onClick={() => toggleExpand(event.eventId)}
            >
              <ExpandMoreIcon style={{ cursor: 'pointer' }} />
            </div>

            {/* Koşullu Genişletme İçeriği */}
            {expandedCard === event.eventId && (
              <div className="expanded-content">
                <p>{event.description}</p>
                <p>
                  <strong>Konum:</strong> {event.location}
                </p>
                <button className="join-event-button">Etkinliğe Katıl</button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
