import '../event/Navbar.css'; // Add styles in a separate CSS file
import React from "react";
import './Home.css';
import './Navbar.css';

function Event() {

  const { userId } = useParams(); // URL'deki userId'yi al
  const [events, setEvents] = useState([]);
  const [isDropdownOpen, setDropdownOpen] = useState(false);
  const [user, setUser] = useState(null); // Kullanıcı verisi
  const [error, setError] = useState(null); // Hata durumu
  const [isLoading, setIsLoading] = useState(true); // Yüklenme durumu
  const [expandedCard, setExpandedCard] = useState(null);
  

  const toggleDropdown = () => {
    setDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = () => {
    window.location.href = '/login';
  };

  const handleProfile = () => {
    window.location.href = `/profile/${userId}`;
  };
  
  return (
    <div className="">
      {/* Navbar Bileşeni */}
      <nav className="navbar">
        <div className="navbar-left">
          <img
            src={uk}
            alt="Profile"
            className="profile-photo"
          />
          <HomeIcon className="profile-photo" />
          <button className="home-button" onClick={() => window.location.href = `/home/${userId}`}>Home</button>
        </div>
        <div className="navbar-right">
          <button className="add-event-button">Etkinlik Ekle</button>
          <div className="profile-menu">
            <img
              src={`/${user.profilePicturePath}`}
              alt="Profile"
              className="profile-photo"
              onClick={toggleDropdown}
            />
            <div className={`dropdown-menu ${isDropdownOpen ? 'show' : ''}`}>
              <button onClick={handleProfile}>Profilim</button>
              <button onClick={handleLogout}>Çıkış</button>
            </div>
          </div>
        </div>
      </nav>    

      
    </div>
  );
}

export default Event;
