import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Google Maps bileşenini yüklemek için react-google-maps kullanabiliriz.
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const mapContainerStyle = {
  width: '100%',
  height: '400px',
};

const center = {
  lat: 40.8333,  // Gebze'nin genel koordinatları
  lng: 29.9167,
};

function Home() {
  const [event, setEvent] = useState(null);

  // Etkinlik bilgilerini almak için API çağrısı
  useEffect(() => {
    axios.get("/api/events")
      .then(response => {
        setEvent(response.data);
      })
      .catch(error => {
        console.error("Etkinlik bilgileri alınamadı", error);
      });
  }, []);

  if (!event) {
    return <div>Yükleniyor...</div>;
  }

  return (
    <div className="Home">
      <div className="event-card">
        <h1>{event.eventName}</h1>
        <p>{event.description}</p>
        <p><strong>Tarih:</strong> {event.date}</p>
        
        <div className="map-container">
          <LoadScript googleMapsApiKey="AIzaSyB76s0W118Nw0l1ImVvdemYCWoYwjPKXbc">
            <GoogleMap
              mapContainerStyle={mapContainerStyle}
              center={center}
              zoom={14}
            >
              <Marker position={center} />
            </GoogleMap>
          </LoadScript>
        </div>
        
        <p><strong>Konum:</strong> {event.location}</p>
      </div>
    </div>
  );
}

export default Home;
