import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Google Maps bileşenini yüklemek için react-google-maps kullanabiliriz.
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const mapContainerStyle = {
  width: '100%',
  height: '400px',
};

// Varsayılan bir merkez koordinatı (örneğin Gebze) kullanabilirsiniz
const defaultCenter = {
  lat: 40.8333,
  lng: 29.9167,
};

function Home() {
  const [events, setEvents] = useState([]);

  // Etkinlikleri almak için API çağrısı
  useEffect(() => {
    axios.get("/events")
      .then(response => {
        setEvents(response.data);  // Etkinlik verilerini güncelliyoruz
      })
      .catch(error => {
        console.error("Etkinlik bilgileri alınamadı", error);
      });
  }, []);

  if (events.length === 0) {
    return <div>Yükleniyor...</div>;
  }

  return (
    <div className="Home">
      <h1>Etkinlikler</h1>
      <div className="event-list">
        {events.map((event) => (
          <div key={event.eventId} className="event-card">
            <h2>{event.eventName}</h2>
            <p>{event.description}</p>
            <p><strong>Tarih:</strong> {event.date}</p>
            <p><strong>Konum:</strong> {event.location}</p>
            
            <div className="map-container">
              <LoadScript googleMapsApiKey="AIzaSyB76s0W118Nw0l1ImVvdemYCWoYwjPKXbc">
                <GoogleMap
                  mapContainerStyle={mapContainerStyle}
                  center={{
                    lat: event.latitude,  // Her etkinlik için enlem bilgisi
                    lng: event.longitude, // Her etkinlik için boylam bilgisi
                  }}
                  zoom={14}
                >
                  <Marker
                    position={{
                      lat: event.latitude,
                      lng: event.longitude,
                    }}
                  />
                </GoogleMap>
              </LoadScript>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
