import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

function EventProfile() {
  const { userId } = useParams(); // URL'deki userId'yi al
  const [selectedTab, setSelectedTab] = useState("joined"); // 'joined' veya 'created'
  const [joinedEvents, setJoinedEvents] = useState([]);
  const [createdEvents, setCreatedEvents] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Katıldığım etkinlikler
    axios
      .get(`/events/joined/${userId}`)
      .then((response) => setJoinedEvents(response.data))
      .catch((error) => console.error("Katıldığım etkinlikler alınamadı:", error));

    // Oluşturduğum etkinlikler
    axios
      .get(`/events/created/${userId}`)
      .then((response) => setCreatedEvents(response.data))
      .catch((error) => console.error("Oluşturduğum etkinlikler alınamadı:", error))
      .finally(() => setIsLoading(false));
  }, [userId]);

  if (isLoading) {
    return <div>Yükleniyor...</div>;
  }

  return (
    <div>
      {/* Butonlar */}
      <div>
        <button onClick={() => setSelectedTab("joined")}>Katıldığım Etkinlikler</button>
        <button onClick={() => setSelectedTab("created")}>Oluşturduğum Etkinlikler</button>
      </div>

      {/* İçerik */}
      {selectedTab === "joined" && (
        <div>
          <h2>Katıldığım Etkinlikler</h2>
          {joinedEvents.length > 0 ? (
            joinedEvents.map((event) => (
              <div key={event.id}>
                <h3>{event.eventName}</h3>
                <p>{event.date}</p>
                <p>{event.location}</p>
              </div>
            ))
          ) : (
            <p>Katıldığınız etkinlik yok.</p>
          )}
        </div>
      )}

      {selectedTab === "created" && (
        <div>
          <h2>Oluşturduğum Etkinlikler</h2>
          {createdEvents.length > 0 ? (
            createdEvents.map((event) => (
              <div key={event.id}>
                <h3>{event.eventName}</h3>
                <p>{event.date}</p>
                <p>{event.location}</p>
              </div>
            ))
          ) : (
            <p>Oluşturduğunuz etkinlik yok.</p>
          )}
        </div>
      )}
    </div>
  );
}

export default EventProfile;
